// Import the getOAuthAccessToken function from the "./get-oauth-access-token" module.
import { getOAuthAccessToken } from "./get-oAuth-access-token";

// Define the async auth function that accepts two parameters: state and authOptions.
async function auth(state, authOptions) {
  // Call the getOAuthAccessToken function with state and an object containing the auth option.
  return getOAuthAccessToken(state, {
    auth: authOptions
  });
}

// Export the auth function for use in other modules.
export {
  auth
};

