// withAuthorizationPrefix is a function that takes a token as an argument
// and returns an authorization prefix string in the format of "bearer {token}"
// or "token {token}", depending on the format of the input token.
// The function first checks if the token is in the correct format by splitting
// it on the "." character and checking if the length of the resulting array is 3.
// If the token is in the correct format, the function returns "bearer {token}",
// otherwise, it returns "token {token}".
function withAuthorizationPrefix(token) {
  if (token.split(/\./).length === 3) {
    // If the token is in the correct format (i.e. has 3 parts separated by "."),
    // return the authorization prefix in the format "bearer {token}"
    return `bearer ${token}`;
  }
  // If the token is not in the correct format, return the authorization prefix
  // in the format "token {token}"
  return `token ${token}`;
}

// Export the withAuthorizationPrefix function for use in other modules
export {
  withAuthorizationPrefix
};
