// Import all types from @octokit/types module
import * as OctokitTypes from "@octokit/types";

// AnyResponse type represents an OctokitResponse with any data type
export type AnyResponse = OctokitTypes.OctokitResponse<any>;

// StrategyInterface type represents a strategy interface with specific type definitions
export type StrategyInterface = OctokitTypes.StrategyInterface<[    Token], [], Authentication>;

// EndpointDefaults type represents endpoint defaults with specific type definitions
export type EndpointDefaults = OctokitTypes.EndpointDefaults;

// EndpointOptions type represents endpoint options with specific type definitions
export type EndpointOptions = OctokitTypes.EndpointOptions;

// RequestParameters type represents request parameters with specific type definitions
export type RequestParameters = OctokitTypes.RequestParameters;

// RequestInterface type represents request interface with specific type definitions
export type RequestInterface = OctokitTypes.RequestInterface;

// Route type represents a route with specific type definitions
export type Route = OctokitTypes.Route;

// Token type represents a string token
export type Token = string;

// OAuthTokenAuthentication type represents an authentication object with token type as "oauth"
export type OAuthTokenAuthentication = {
    type: "token";    tokenType: "oauth";    token: Token;
};

// InstallationTokenAuthentication type represents an authentication object with token type as "installation"
export type InstallationTokenAuthentication = {
    type: "token";    tokenType: "installation";    token: Token;
};

// AppAuthentication type represents an authentication object with token type as "app"
export type AppAuthentication = {
    type: "token";    tokenType: "app";    token: Token;
};

// UserToServerAuthentication type represents an authentication object with token type as "user-to-server"
export type UserToServerAuthentication = {
    type: "token";    tokenType: "user-to-server";    token: Token;
};

// Authentication type represents an authentication object with possible token types
export type Authentication = OAuthTokenAuthentication | InstallationTokenAuthentication | AppAuthentication | UserToServerAuthentication
