// Define the function signature and return type
export default function getBufferResponse(response: Response): Promise<ArrayBuffer>;

// Inside the function, it likely does something like this:

// Check if the response is ok
if (!response.ok) {
  // If not, reject the promise with a error message
  return Promise.reject(new Error(`Response error: ${response.status} ${response.statusText}`));
}

// Otherwise, read the response as an ArrayBuffer
return response.arrayBuffer();

