/**
 * Represents an error that occurred during a request.
 */
export type RequestError = {
    /**
     * The name of the error.
     */
    name: string;

    /**
     * The HTTP status code associated with the error.
     */
    status: number;

    /**
     * A URL pointing to documentation that provides more information about the error.
     */
    documentation_url: string;

    /**
     * An array of error objects, each providing more details about a specific aspect of the error.
     */
    errors?: Array<{
        /**
         * The resource associated with the error.
         */
        resource: string;

        /**
         * The error code.
         */
        code: string;

        /**
         * The field associated with the error.
         */
        field: string;

        /**
         * An optional message providing more information about the error.
         */
        message?: string;
    }>;
};
