import { Callback, Handler } from "../handler"; // Importing Callback and Handler types from "../handler" module

// Defining the type for Auto Scaling Scale In Handler, which takes in AutoScalingScaleInEvent and returns AutoScalingScaleInResult
export type AutoScalingScaleInHandler = Handler<AutoScalingScaleInEvent, AutoScalingScaleInResult>;
// Defining the type for Auto Scaling Scale In Callback, which takes in AutoScalingScaleInResult
export type AutoScalingScaleInCallback = Callback<AutoScalingScaleInResult>;

// Defining the type for Auto Scaling Instance Market Option, which can be either "spot" or "on-demand"
export type AutoScalingInstanceMarketOption = "spot" | "on-demand";

// Defining the type for Auto Scaling Scale In Cause, which can be one of the following: "SCALE_IN", "INSTANCE_REFRESH", "MAX_INSTANCE_LIFETIME", "REBALANCE"
export type AutoScalingScaleInCause = "SCALE_IN" | "INSTANCE_REFRESH" | "MAX_INSTANCE_LIFETIME" | "REBALANCE";

// Defining the shape of Auto Scaling Termination Request, which contains AvailabilityZone, Capacity, and InstanceMarketOption properties
export interface AutoScalingTerminationRequest {
    AvailabilityZone: string;
    Capacity: number;
    InstanceMarketOption: AutoScalingInstanceMarketOption;
}

// Defining the shape of Auto Scaling Instance Record, which contains AvailabilityZone, InstanceId, InstanceType, and InstanceMarketOption properties
export interface AutoScalingInstanceRecord {
    AvailabilityZone: string;
    InstanceId: string;
    InstanceType: string;
    InstanceMarketOption: AutoScalingInstanceMarketOption;
}

