// Import necessary types for working with AWS Lambda and CloudFront events
import type { CloudFrontEvent, CloudFrontRequest, CloudFrontResultResponse } from "../common/cloudfront";
import type { Callback, Handler } from "../handler";

// Define a type for a CloudFront request handler function
export type CloudFrontRequestHandler = Handler<CloudFrontRequestEvent, CloudFrontRequestResult>;

// Define a type for a CloudFront request callback function
export type CloudFrontRequestCallback = Callback<CloudFrontRequestResult>;

// Define an interface for a CloudFront request event record
export interface CloudFrontRequestEventRecord {
    // The CloudFront event object, which contains information about the request
    cf: CloudFrontEvent & {
        // The CloudFront request object, which contains information about the viewer request or origin request
        request: CloudFrontRequest;
    };
}

