import * as tls from 'node:tls';

const socket = tls.connect(8000, { key: 'key.pem', cert: 'cert.pem' }, () => {
  console.log('connected');
});

socket.on('data', (data) => {
  console.log(data.toString());
});

socket.on('end', () => {
  console.log('disconnected');
});


