/**
 * The `node:trace_events` module provides a mechanism to centralize tracing
 * information generated by V8, Node.js core, and userspace code.
 * ...
 */

// The 'trace_events' module is declared, which contains types and interfaces
// related to the Node.js 'trace_events' module.
declare module "trace_events" {
  // The 'Tracing' interface represents a tracing object used to enable or
  // disable tracing for sets of categories.
  interface Tracing {
    // A read-only property that returns a comma-separated list of the trace
    // event categories covered by this 'Tracing' object.
    readonly categories: string;

    // A method that disables this 'Tracing' object.
    disable(): void;

    // A method that enables this 'Tracing' object for the set of categories
    // covered by the 'Tracing' object.
    enable(): void;

    // A read-only property that returns 'true' only if the 'Tracing' object
    // has been enabled.
    readonly enabled: boolean;
  }

  // The 'CreateTracingOptions' interface represents the options for creating
  // a 'Tracing' object.
  interface CreateTracingOptions {
    // An array of trace category names.
    categories: string[];
  }

  // A function that creates and returns a 'Tracing' object for the given set
  // of 'categories'.
  function createTracing(options: CreateTracingOptions): Tracing;

  // A function that returns a comma-separated list of all currently-enabled
  // trace event categories.
  function getEnabledCategories(): string | undefined;
}

// The 'node:trace_events' module is declared, which re-exports the 'trace_events'
// module.
declare module "node:trace_events" {
  export * from "trace_events";
}
