/**
 * The Deprecation class is a custom error class that extends the built-in Error class.
 * It is used to indicate that a particular feature or method is deprecated and should no longer be used.
 */
export class Deprecation extends Error {
  /**
   * Creates a new instance of the Deprecation class with the specified message.
   *
   * @param {string} message - The error message to display when the deprecation error is thrown.
   */
  constructor(message) {
    // Call the constructor of the superclass (Error) with the specified message
    super(message);

    // Maintain proper stack trace for the error (only available on V8)
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, this.constructor);
    }

    // Set the name property of the error to 'Deprecation'
    this.name = 'Deprecation';
  }
}
