// Define a new class named 'Deprecation' that extends the built-in 'Error' class
class Deprecation extends Error {
  // Constructor function for the 'Deprecation' class
  constructor(message) {
    // Call the constructor of the superclass ('Error') with the provided message
    super(message); // Maintains proper stack trace (only available on V8)

    /* istanbul ignore next */

    // If the 'captureStackTrace' method is available on the 'Error' object, use it to capture a stack trace
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, this.constructor);
    }

    // Set the name property of the current instance to 'Deprecation'
    this.name = 'Deprecation';
  }

}

// Export the 'Deprecation' class for use in other modules
export { Deprecation };
