'use strict';

// Function to calculate the number of bytes required to store a parameter of a given size in bits
function getParamSize(keySize) {
	var result = ((keySize / 8) | 0) + (keySize % 8 === 0 ? 0 : 1);
	return result;
}

// Object to store the number of bytes required for each algorithm's parameter
var paramBytesForAlg = {
	ES256: getParamSize(256),
	ES384: getParamSize(384),
	ES512: getParamSize(521)
};

// Function to retrieve the number of bytes required for an algorithm's parameter
function getParamBytesForAlg(alg) {
	var paramBytes = paramBytesForAlg[alg];
	if (paramBytes) {
		return paramBytes;
	}

	// Throw an error if the algorithm is not recognized
	throw new Error('Unknown algorithm "' + alg + '"');
}

// Export the getParamBytesForAlg function for use in other modules
module.exports = getParamBytesForAlg;

