'use strict';

/**
 * This module exports a custom error class called `Error`. This class can be used to create user-defined errors that can include additional properties beyond those provided by the built-in JavaScript Error class.
 *
 * @module CustomError
 * @type {class}
 * @property {string} name - The name of the error.
 * @property {string} message - The error message.
 * @property {string[]} stack - An array of strings that represent the call stack at the time the error was created.
 * @property {object} [details] - An optional object that can contain any additional error details.
 * @example
 * const CustomError = require('./CustomError');
 * const error = new CustomError('Something went wrong');
 * console.error(error.message); // Output: Something went wrong
 */
module.exports = Error;

