'use strict';

// Create an empty object 'test' with a property 'foo' also set to an empty object
var test = {
	foo: {}
};

// Store a reference to the global Object constructor in $Object variable
var $Object = Object;

// Export a function that checks if the object has a non-null prototype (i.e., has a prototype chain)
module.exports = function hasProto() {
	// Create an object with a __proto__ property set to the 'test' object
	return { __proto__: test }.foo === test.foo && 
		// Check if the object created above is not an instance of the Object constructor when its __proto__ is set to null
		!({ __proto__: null } instanceof $Object);
};
