'use strict';

// Import required modules: the 'tape' module for testing and the 'hasProto' module to be tested
var test = require('tape');
var hasProto = require('../');

// Start the 'hasProto' test suite
test('hasProto', function (t) {

  // Initialize the 'result' variable with the result of calling 'hasProto()'
  var result = hasProto();

  // Assert that the type of 'result' is 'boolean' and store the actual result value in the tape result object
  t.equal(typeof result, 'boolean', 'returns a boolean (' + result + ')');

  // Initialize the 'obj' variable as an object with its prototype set to null
  var obj = { __proto__: null };

  // Check if 'result' is true
  if (result) {

    // Assert that the 'toString' property does not exist in 'obj'
    t.notOk('toString' in obj, 'null object lacks toString');
  } else {

    // Assert that the 'toString' property exists in 'obj'
    t.ok('toString' in obj, 'without proto, null object has toString');

    // Assert that the prototype of 'obj' is null (ignoring ESLint rule 'no-proto')
    t.equal(obj.__proto__, null);
  }

  // End the test and report the results
  t.end();
});

