// Load the 'mime' module that contains functions for determining the MIME type of a file
var mime = require('./mime.js');

// Get the file name from the command-line arguments
var file = process.argv[2];

// Use the 'mime.lookup' function to get the MIME type of the file
var type = mime.lookup(file);

// Write the MIME type to the console and add a newline character
process.stdout.write(type + '\n');

