// Define a new class FetchBaseError that extends the built-in Error class
export class FetchBaseError extends Error {
	// Constructor for FetchBaseError class with two parameters: message and type
	constructor(message, type) {
		// Call the constructor of the superclass (Error) with the message parameter
		super(message);

		// Hide custom error implementation details from end-users
		Error.captureStackTrace(this, this.constructor);

		// Initialize the type property with the type parameter
		this.type = type;
	}

	// Define a getter for the name property that returns the name of the constructor
	get name() {
		return this.constructor.name;
	}

	// Define a getter for the [Symbol.toStringTag] property that returns the name of the constructor
	get [Symbol.toStringTag]() {
		return this.constructor.name;
	}
}

