'use strict';

// Import the 'inspect' module, which provides a customizable way to
// stringify JavaScript objects. This will be used to log the object to
// the console.
var inspect = require('../');

// Create an object with some properties
var obj = {
  a: 1, // a number value
  b: [3, 4] // an array of numbers
};

// Set the 'c' property of the object to be a reference to the object itself,
// creating a circular reference
obj.c = obj;

// Log the object to the console using the 'inspect' module. This will
// provide a string representation of the object that can be used for
// debugging purposes.
console.log(inspect(obj));
