'use strict'; // Enforces strict parsing rules and throws errors when expressions that are usually allowed by JavaScript are used

// Require the 'core-js' module to ensure compatibility with older JavaScript engines
require('core-js');

// Require the 'inspect' module for converting objects to a human-readable format
const inspect = require('./');

// Require the 'tape' module for testing
const test = require('tape');

// Test suite for Maps
test('Maps', function (t) {
    // Assert that the 'inspect' function correctly converts a Map object to a string
    t.equal(inspect(new Map([[1, 2]])), 'Map (1) {1 => 2}');
    t.end(); // End the test case
});

// Test suite for WeakMaps
test('WeakMaps', function (t) {
    // Assert that the 'inspect' function correctly converts a WeakMap object to a string
    t.equal(inspect(new WeakMap([[{}, 2]])), 'WeakMap { ? }');
    t.end(); // End the test case
});

// Test suite for Sets
test('Sets', function (t) {
    // Assert that the 'inspect' function correctly converts a Set object to a string
    t.equal(inspect(new Set([[1, 2]])), 'Set (1) {[ 1, 2 ]}');
    t.end(); // End the test case
});

// Test suite for WeakSets
test('WeakSets', function (t) {
    // Assert that the 'inspect' function correctly converts a WeakSet object to a string
    t.equal(inspect(new WeakSet([[1, 2]])), 'WeakSet { ? }');
    t.end(); // End the test case
});

