'use strict'; // strict mode is enabled to disallow certain actions within the script

// require the 'inspect' module from the parent directory
var inspect = require('../');

// require the 'tape' module for testing
var test = require('tape');

// get a reference to the global object (either 'window' in a browser or 'global' in Node.js)
var globalThis = require('globalthis')();

// define a test case for the 'global object'
test('global object', function (t) {
    /* eslint-env browser */
    // set the 'expected' variable to the expected type of the global object
    // (either 'globalThis' in Node.js or 'Window' in a browser)
    var expected = typeof window === 'undefined' ? 'globalThis' : 'Window';

    // run the test and check if the output of the 'inspect' function
    // matches the expected output
    t.equal(
        inspect([globalThis]), // pass the global object as an argument to the 'inspect' function
        '[ { [object ' + expected + '] } ]' // expected output
    );

    // end the test case
    t.end();
});

