// Import the 'tape' module for testing and the 'inspect' module for inspecting values
var test = require('tape');
var inspect = require('../');

// Define an array 'xs' with some values, and also some undefined/hole values
var xs = ['a', 'b'];
xs[5] = 'f';
xs[7] = 'j';
xs[8] = 'k';

// Define a test case using the 'tape' module to check if the 'inspect' module correctly displays arrays with holes/undefined values
test('holes', function (t) {
    // Specify that this test case has 1 assertion
    t.plan(1);

    // Use the 'inspect' module to inspect the 'xs' array and compare it to the expected string representation
    t.equal(
        // The 'inspect' function call to inspect the 'xs' array
        inspect(xs),

        // The expected string representation of the 'xs' array with holes/undefined values
        "[ 'a', 'b', , , , 'f', , 'j', 'k' ]"
    );

    // Call the 'end' method on the test function to indicate that this test case is complete
    t.end();
});
