// Importing the Tape testing framework and the custom 'inspect' function
var test = require('tape');
var inspect = require('../');

// Defining an object with some unusual string values
var obj = { x: 'a\r\nb', y: '\x05! \x1f \x12' };

// Writing a test for the 'inspect' function using Tape
test('interpolate low bytes', function (t) {
    // The test plan indicates that we expect one assertion to be made
    t.plan(1);

    // The assertion checks whether the 'inspect' function correctly formats the 'obj' object
    t.equal(
        // The expected value is a string that represents the formatted object
        "{ x: 'a\\r\\nb', y: '\\x05! \\x1F \\x12' }",

        // The actual value is the result of calling the 'inspect' function with the 'obj' object as an argument
        inspect(obj)
    );

    // When the test is complete, Tape will report whether the assertion passed or failed
});
