// Import the compare function from the compare module
const compare = require('./compare')

// Define the compareLoose function, which is a loose version of the compare function
/**
 * A loose version of the compare function, which compares two values with relaxed equality.
 * This function is a wrapper around the compare function, with the loose option set to true.
 * @param {*} a - The first value to compare.
 * @param {*} b - The second value to compare.
 * @returns {boolean} True if the values are equal, false otherwise.
 */
const compareLoose = (a, b) => compare(a, b, true)

// Export the compareLoose function for use in other modules
module.exports = compareLoose
