// Import the compare function from the './compare' module.
const compare = require('./compare')

// Export a function that checks if the first argument is less than or equal to the second argument, optionally using loose comparison.
// The function takes three arguments:
// - a: the first value to compare
// - b: the second value to compare
// - loose (optional): a boolean indicating whether to use loose comparison (default is false)
// The function returns a boolean indicating whether a is less than or equal to b.
const lte = (a, b, loose = false) => compare(a, b, loose) <= 0

// Export the lte function for use in other modules.
module.exports = lte
