/**
 * Exports a function that checks if two values are not equal, using the `compare`
 * function from the './compare' module.
 *
 * @module neq
 * @param {*} a - The first value to compare.
 * @param {*} b - The second value to compare.
 * @param {boolean} loose - Whether to use loose equality comparison (`==`) instead
 * of strict equality comparison (`===`). Default is `false`.
 * @returns {boolean} `true` if the values are not equal, `false` otherwise.
 */
const compare = require('./compare')
const neq = (a, b, loose) => compare(a, b, loose) !== 0
module.exports = neq
