// Import the compareBuild function from the compare-build module
const compareBuild = require('./compare-build')

// The rsort function takes in two arguments: a list and a boolean flag called loose
// The list is expected to be an array of items that can be passed to compareBuild
// The loose flag is an optional parameter that defaults to false
// The function returns a new array that contains the same items as the input list,
// but sorted in reverse order based on the comparison result of the compareBuild function
const rsort = (list, loose = false) => list.sort((a, b) => compareBuild(b, a, loose))

// Export the rsort function so it can be used in other modules
module.exports = rsort

