/**
 * This module exports a function that sorts an array of build objects.
 * The comparison of the build objects is done using the `compareBuild` function from the `compare-build` module.
 *
 * @module sort-builds
 * @param {Array<Object>} list - The array of build objects to be sorted.
 * @param {boolean} loose - A flag to indicate whether the comparison should be loose (default is false).
 * @returns {Array<Object>} The sorted array of build objects.
 */
const compareBuild = require('./compare-build')
const sort = (list, loose = false) => list.sort((a, b) => compareBuild(a, b, loose))
module.exports = sort
