const parse = require('./parse')

/**
 * Validates a given version string and returns the version number if it is valid,
 * or null if it is invalid.
 *
 * @param {string} version - The version string to validate.
 * @param {object} options - An optional object containing options for parsing the version string.
 * @return {string|null} The validated version number, or null if the version string is invalid.
 */
const valid = (version, options) => {
  const v = parse(version, options)
  return v ? v.version : null
}

module.exports = valid
