// Import the Range class from the '../classes/range' module.
const Range = require('../classes/range')

// Define the intersects function, which takes in two ranges (r1 and r2) and an options object.
// The function returns a boolean indicating whether or not the two ranges intersect.
const intersects = (r1, r2, options) => {
  // Create new Range objects for r1 and r2, passing in the options object.
  // This ensures that any default options are applied and that the ranges are properly initialized.
  r1 = new Range(r1, options)
  r2 = new Range(r2, options)

  // Call the intersects method on the r1 Range object, passing in the r2 Range object and the options object.
  // The intersects method returns a boolean indicating whether or not the two ranges intersect.
  return r1.intersects(r2, options)
}

// Export the intersects function for use in other modules.
module.exports = intersects
