// This code exports two functions: setGlobalOrigin and getGlobalOrigin
export {
	// The setGlobalOrigin function sets the global origin to the provided value,
	// which can be a string, a URL object, or undefined.
	setGlobalOrigin,
	// The getGlobalOrigin function retrieves the current global origin as a URL object
	// or undefined if no origin has been set.
	getGlobalOrigin
}
  
// This is the type signature for the setGlobalOrigin function, which takes
// an argument of type string | URL | undefined and returns nothing (void).
declare function setGlobalOrigin(origin: string | URL | undefined): void;

// This is the type signature for the getGlobalOrigin function, which takes
// no arguments and returns a URL object or undefined.
declare function getGlobalOrigin(): URL | undefined;
