import Pool from "./pool"

// The PoolStats class provides statistics about a Pool instance.
export default PoolStats

// The PoolStats class constructor takes a Pool object as its only argument.
declare class PoolStats {
  // Initializes a new PoolStats instance with the given Pool.
  constructor(pool: Pool);
  
  // The number of open socket connections in this pool.
  connected: number;
  
  // The number of open socket connections in this pool that do not have an active request.
  free: number;
  
  // The number of pending requests across all clients in this pool.
  pending: number;
  
  // The number of queued requests across all clients in this pool.
  queued: number;
  
  // The number of currently active requests across all clients in this pool.
  running: number;
  
  // The number of active, pending, or queued requests across all clients in this pool.
  size: number;
}

