const numbers = [1, 2, 3, 4, 5];
const stream = new ReadableStream({
  start(controller) {
    controller.enqueue(numbers[0]);
  },
  pull(controller) {
    if (numbers.length > 0) {
      controller.enqueue(numbers.shift());
    } else {
      controller.close();
    }
  }
});

const reader = stream.getReader();

reader.read().then(({value, done}) => {
  if (value) {
    console.log(value);
  }
  if (done) {
    console.log('Stream is closed');
  }
});

