'use strict';

/**
 * This module exports a custom error class called `Error`. This class can be used to create user-defined errors in the application.
 *
 * @example
 * const MyCustomError = new Error('Something went wrong');
 * throw MyCustomError;
 *
 * @module
 * @type {class}
 * @param {string} message - A description of the error.
 */
module.exports = Error;
