const GetIntrinsic = require('./get-intrinsic');

// Retrieve the Array intrinsic object
const ArrayIntrinsic = GetIntrinsic('%Array%');
console.log(ArrayIntrinsic === Array); // true

// Retrieve the Math.floor intrinsic function
const floorIntrinsic = GetIntrinsic('%Math.floor%');
console.log(floorIntrinsic === Math.floor); // true

// Try to retrieve a non-existent intrinsic (throws a TypeError)
GetIntrinsic('%NonExistent%');

// Try to retrieve a non-existent intrinsic, allowing missing (returns undefined)
GetIntrinsic('%NonExistent%', true); // undefined
