// Import the 'inspect' function from the relative path '../'
var inspect = require('../');

// Import the 'tape' module for testing
var test = require('tape');

// Define the first test case named 'circular'
test('circular', function (t) {
    // Set the test plan to 2, indicating that there will be two assertions
    t.plan(2);

    // Create an object 'obj' with properties 'a' and 'b'
    var obj = { a: 1, b: [3, 4] };

    // Add a new property 'c' to the 'obj' object, which references the 'obj' itself
    obj.c = obj;

    // Assert that the output of the 'inspect' function for the 'obj' object is equal to the expected string
    t.equal(inspect(obj), '{ a: 1, b: [ 3, 4 ], c: [Circular] }');

    // Create a new object 'double' with properties 'a', 'b', and 'b.inner'
    var double = {};
    double.a = [double];
    double.b = {};

    // Add a new property 'inner' to the 'double.b' object, which references the 'double.b' object itself
    double.b.inner = double.b;

    // Add a new property 'obj' to the 'double.b' object, which references the 'double' object itself
    double.b.obj = double;

    // Assert that the output of the 'inspect' function for the 'double' object is equal to the expected string
    t.equal(inspect(double), '{ a: [ [Circular] ], b: { inner: [Circular], obj: [Circular] } }');
});

