// Require the 'inspect' module from the parent directory
var inspect = require('../');

// Require the 'tape' module for testing
var test = require('tape');

// Define the 'deep' test suite
test('deep', function (t) {
    // Set the number of tests to 4
    t.plan(4);

    // Define a nested array 'obj'
    var obj = [[[[[[500]]]]]];

    // Assert that the string representation of 'obj' is equal to '[ [ [ [ [ [Array] ] ] ] ] ]'
    t.equal(inspect(obj), '[ [ [ [ [ [Array] ] ] ] ] ]');

    // Assert that the string representation of 'obj' with a depth limit of 4 is equal to '[ [ [ [ [Array] ] ] ] ]'
    t.equal(inspect(obj, { depth: 4 }), '[ [ [ [ [Array] ] ] ] ]');

    // Assert that the string representation of 'obj' with a depth limit of 2 is equal to '[ [ [Array] ] ]'
    t.equal(inspect(obj, { depth: 2 }), '[ [ [Array] ] ]');

    // Assert that the string representation of a nested object array with a depth limit of 3 is equal to '[ [ [ [Object] ] ] ]'
    t.equal(inspect([[[{ a: 1 }]]], { depth: 3 }), '[ [ [ [Object] ] ] ]');
});

