'use strict';

// Import the 'stringify' module which is used to convert a value to a JSON-formatted string
const stringify = require('./stringify');

// Import the 'parse' module which is used to convert a JSON-formatted string to a JavaScript object
const parse = require('./parse');

// Import the 'formats' module which contains various format options for the stringify and parse functions
const formats = require('./formats');

// Export an object containing the 'formats', 'parse', and 'stringify' functions
module.exports = {
  // The 'formats' property is an object containing various format options for the stringify and parse functions
  formats: formats,

  // The 'parse' function is used to convert a JSON-formatted string to a JavaScript object
  parse: parse,

  // The 'stringify' function is used to convert a value to a JSON-formatted string
  stringify: stringify
};
