/**
 * Module exports.
 */

// The 'vary' function is exported, which is the main functionality of this module.
// Additionally, the 'append' function is also exported, which is used internally by 'vary'.
module.exports = vary
module.exports.append = append

/**
 * RegExp to match field-name in RFC 7230 sec 3.2
 *
 * field-name    = token
 * token         = 1*tchar
 * tchar         = "!" / "#" / "$" / "%" / "&" / "'" / "*"
 *               / "+" / "-" / "." / "^" / "_" / "`" / "|" / "~"
 *               / DIGIT / ALPHA
 *               ; any VCHAR, except delimiters
 */

// A regular expression is defined to match valid field names as per the RFC 7230 specification.
var FIELD_NAME_REGEXP = /^[!#$%&'*+\-.^_`|~0-9A-Za-z]+$/

/**
 * Append a field to a vary header.
 *
 * @param {String} header - The vary header string.
 * @param {String|Array} field - The field to append to the vary header.
 * @return {String} - The updated vary header string.
 * @public
 */

// The 'append' function is used to append a field to a vary header.
function append (header, field) {
  if (typeof header !== 'string') {
    throw new TypeError('header argument is required')
  }

  if (!field) {
    throw new TypeError('field argument is required')
  }

  // If the 'field' argument is not an array, convert it to an array.
  var fields = !Array.isArray(field)
    ? parse(String(field))
    : field

  // Assert that all field names in the 'fields' array are valid.
  for (var j = 0; j < fields.length; j++) {
    if (!FIELD_NAME_REGEXP.test(fields[j])) {
      throw new TypeError('field argument contains an invalid header name')
    }
  }

  // If the 'header' argument is '*', return it as is.
  if (header === '*') {
    return header
  }

  // Parse the 'header' argument into an array of fields.
  var val = header
  var vals = parse(header.toLowerCase())

  // If either the 'fields' array or the parsed 'header' array contains '*', return '*'.
  if (fields.indexOf('*') !== -1 || vals.indexOf('*') !== -1) {
    return '*'
  }

  // Iterate over the 'fields' array and append each field to the 'val' string.
  for (var i = 0; i < fields.length; i++) {
    var fld = fields[i].toLowerCase()

    // Append the field to the 'val' string if it is not already present.
    if (vals.indexOf(fld) === -1) {
      vals.push(fld)
      val = val
        ? val + ', ' + fields[i]
        : fields[i]
    }
  }

  return val
}

/**
 * Parse a vary header into an array.
 *
 * @param {String} header - The vary header string.
 * @return {Array} - An array of fields in the vary header.
 * @private
 */

// The 'parse' function is used internally by 'append' to parse a vary header into an array of fields.
function parse (header) {
  var end = 0
  var list = []
  var start = 0

  // Iterate over the 'header' string and gather tokens into an array.
  for (var i = 0, len = header.length; i < len; i++) {
    switch (header.charCodeAt(i)) {
      case 0x20: /*   */
        if (start === end) {
          start = end = i + 1
        }
        break
      case 0x2c: /* , */
        list.push(header.substring(start, end))
        start = end = i + 1
        break
      default:
        end = i + 1
        break
    }
  }

  // Add the final token to the array.
  list.push(header.substring(start, end))

  return list
}

